    case 'getcontactslist':
        $top = 25;
        if ($_POST['type'] == 'all_contacts') {
            $top = 40;
        }
        $sz = rand(10, $top);
        $list = array();
        for ($i = 0; $i < $sz; ++$i) {
            $o['uid'] = $i + 1;
            $o['name'] = genUname($i + 1);
            $o['login'] = 'login_'.($i + 1);
            $online = rand(0, 1);
            if ($_POST['online'] == 1) {
                $online = 1;
            }
            $o['isOnline'] = $online;
            $o['avatar'] = rand(0, 1) ? '/images/temp/small-pic1.png' : '/images/temp/small-pic.gif';
            $o['isEmpl'] = rand(0, 1);
            $list[] = $o;
        }
        json('list', $list);
        break;
    case 'gethistory':
        $list = array();
        if (!is_array($_SESSION['qchtesthistory_'.$_POST['uid']])) {
            $sz = rand(1, 20);        
            $data = explode('.', $text);
            for ($i = 0; $i < $sz; ++$i) {
                $o['text'] = $data[rand(0, count($data) - 1)].'.';
                $o['files'] = array();
                $o['incoming'] = rand(0, 1);
                $o['time'] = date('d.m.Y');
                $list[] = $o;
            }
            $_SESSION['qchtesthistory_'.$_POST['uid']] = $list;
        } else {
            $list = $_SESSION['qchtesthistory_'.$_POST['uid']];
        }
        json('list', $list);
        break;
    case 'addmessage':
        //unset($_SESSION["qchtesthistory_".$_POST["uid"]]);
        if (!is_array($_SESSION['qchtesthistory_'.$_POST['uid']])) {
            $_SESSION['qchtesthistory_'.$_POST['uid']] = array();
        }
        $o['text'] = $_POST['text'];
        $o['files'] = array();
        $o['incoming'] = 0;
        $o['time'] = date('d.m.Y');
        $_SESSION['qchtesthistory_'.$_POST['uid']][] = $o;

        $data = explode('.', $text);
        $o['text'] = $data[rand(0, count($data) - 1)].'.';
        $o['files'] = array();
        $o['incoming'] = 1;
        $o['time'] = date('d.m.Y');
        $_SESSION['qchtesthistory_'.$_POST['uid']][] = $o;

        json('text', $_POST['text'], 'date', date('d.m.Y'));
        break;
}

function genUname($id)
{
    if ($_SESSION["testqch$id"]) {
        return $_SESSION["testqch$id"];
    }
    $glas = iconv('UTF-8', 'WINDOWS-1251//IGNORE', 'аеёиоуэюя');
    $glasU = iconv('UTF-8', 'WINDOWS-1251//IGNORE', 'АЕЁИОУЭЮЯ');
    $soglas = iconv('UTF-8', 'WINDOWS-1251//IGNORE', 'бвгджзклмнпрстфхцчшщ');
    $soglasU = iconv('UTF-8', 'WINDOWS-1251//IGNORE', 'БВГДЖЗКЛМНПРСТФХЦЧШЩ');
    $nameLen = rand(3, 8);
    $snameLen = rand(3, 15);

    $gCount = 0;
    $sCount = 0;
    $name = '';
    $prev = '';
    for ($i = 0; $i < $nameLen; ++$i) {
        $s = $glas;
        if (rand(0, 10) % 2 == 0) {
            $s = $soglas;
        }

        if ($s == $soglas) {
            ++$sCount;            
            if ($sCount > 1) {
                $s = $glas;
                ++$gCount;
            }
        }

        if ($s == $glas) {
            ++$gCount;            
            if ($gCount > 1) {
                $s = $soglas;
                ++$sCount;
            }
        }

        if ($gCount > 1 && $sCount > 1) {
            if (strpos($glas, $prev) !== false) {
                $s = $soglas;
            }
            if (strpos($soglas, $prev) !== false) {
                $s = $glas;
            }
            $gCount = $sCount = 0;
        }

        if ($i == 0) {
            if ($s == $glas) {
                $s = $glasU;
            }
            if ($s == $soglas) {
                $s = $soglasU;
            }
        }
        $j = rand(0, strlen($s) - 1);
        $k = 0;
        while (strpos($name, $s[$j]) !== false) {
            $j = rand(0, strlen($s) - 1);
            ++$k;
            if ($k > strlen($glas)) {
                break;
            }
        }
        $prev = $s[$j];
        $name .= $prev;
    }
    $name .= ' ';
    $gCount = 0;
    $sCount = 0;
    for ($i = 0; $i < $snameLen; ++$i) {
        $s = $glas;
        if (rand(0, 10) % 2 == 0) {
            $s = $soglas;
        }

        if ($s == $soglas) {
            ++$sCount;            
            if ($sCount > 1) {
                $s = $glas;
                ++$gCount;
            }
        }

        if ($s == $glas) {
            ++$gCount;            
            if ($gCount > 1) {
                $s = $soglas;
                ++$sCount;
            }
        }

        if ($gCount > 1 && $sCount > 1) {
            if (strpos($glas, $prev) !== false) {
                $s = $soglas;
            }
            if (strpos($soglas, $prev) !== false) {
                $s = $glas;
            }
            $gCount = $sCount = 0;
        }

        if ($i == 0) {
            if ($s == $glas) {
                $s = $glasU;
            }
            if ($s == $soglas) {
                $s = $soglasU;
            }
        }
        $j = rand(0, strlen($s) - 1);
        $k = 0;
        while (strpos($name, $s[$j]) !== false) {
            $j = rand(0, strlen($s) - 1);
            ++$k;
            if ($k > strlen($glas)) {
                break;
            }
        }
        $prev = $s[$j];
        $name .= $prev;
    }
    $name = iconv('WINDOWS-1251', 'UTF-8//IGNORE', $name);
    $_SESSION["testqch$id"] = $name;

    return $name;
}

/**
 * выдает в поток данные в json формате
 * четные аргументы - ключи, нечетные - значения
 * в данных всегда присутствует status => ok.
 * */
function json()
{
    $sz = func_num_args();
    for ($i = 0; $i < $sz; ++$i) {
        if ($i + 1 < $sz) {
            $data[func_get_arg($i)] = func_get_arg($i + 1);
            ++$i;
        }
    }
    echo(json_encode($data));
    exit;
}
